/**
 * 辅助工具，输出彩色控制台信息。
 * @packageDocumentation
 */
import { LoggerOptions } from "./options";
declare type Mode = "dev" | "prod";
/**
 * 日志工具类
 */
export default class Logger {
    options?: Partial<LoggerOptions>;
    /**
     * 启用
     */
    enable: boolean;
    /**
     * 当前模式，开发模式输出 Debug 信息
     */
    mode: Mode;
    constructor(options?: Partial<LoggerOptions>);
    /**
     * 打印消息
     * @param type 类型
     * @param msg 消息
     */
    print(type: string, msg: any): void;
    /**
     * 输出成功信息（绿色）
     * @param msg 文本
     */
    success(msg: any): void;
    /**
     * 输出警告信息（黄色）
     * @param msg 文本
     */
    warning(msg: any): void;
    /**
     * 输出错误信息（红色）
     * @param msg 文本
     */
    error(msg: any): void;
    /**
     * 输出提示信息（蓝色）
     * @param msg 文本
     */
    info(msg: any): void;
    /**
     * 输出 Debug 信息（cyan）
     * @param msg
     */
    debug(msg: any): void;
}
export {};
