/**
 * 防抖函数：函数被触发后 n 秒执行回调，n 秒内被触发，则重新计时
 * @param fn 执行的函数
 * @param delay ms 延迟执行时间
 */
export declare function debounce(fn: Function, delay?: number): (...args: any) => void;
/**
 * 节流函数：单位时间内，只有一次触发事件的回调函数执行
 * @param fn
 * @param threshold ms 单位时间
 */
export declare function throttle(fn: Function, threshold?: number): (...args: any) => void;
/**
 * 控制台输出样式信息
 * @param name 名称
 * @param link 链接
 * @param color 颜色
 */
export declare function consoleInfo(name: string, link: string, color?: string): void;
